<template>    <Head>        <title>Course Page</title>    </Head>    <div class="course-page bg-gray-50 min-h-screen p-6">        <!-- Header Section -->        <header class="text-center mb-8">            <h1 class="text-3xl md:text-4xl font-bold text-gray-800">                {{ course.name }} <span class="text-indigo-500">({{ course.code }})</span>            </h1>        </header>        <!-- Course Tabs -->        <div class="mb-8">            <div class="border-b border-gray-200">                <nav aria-label="Course sections" class="flex space-x-8">                    <a                        v-for="tab in tabs"                        :key="tab.id"                        :class="[                    'py-4 px-1 border-b-2 font-medium text-sm focus:outline-none',                    currentTab === tab.id                        ? 'border-indigo-500 text-indigo-600'                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'                ]"                        :href="tab.url"                    >                        {{ tab.name }}                    </a>                </nav>            </div>        </div>        <!-- Weekly Units Section -->        <section>            <h2 class="text-2xl font-bold text-gray-800 mb-4">Weekly Content</h2>            <div                v-for="(week, index) in weeks"                :key="week.id"                class="mb-4 bg-white shadow rounded-md overflow-hidden"            >                <!-- Week Header -->                <div                    class="flex justify-between items-center p-4 bg-indigo-500 text-white cursor-pointer hover:bg-indigo-600"                    @click="toggleWeek(index)"                >                    <div>                        <h2 class="text-lg font-semibold">Week {{ week.week_number }}: {{ week.title }}</h2>                    </div>                    <i                        :class="[ 'fas', week.isOpen ? 'fa-chevron-up' : 'fa-chevron-down', 'transition-transform duration-300' ]"                    ></i>                </div>                <!-- Week Content -->                <div v-show="week.isOpen" class="p-4 bg-gray-50">                    <!-- Content Links -->                    <div class="grid md:grid-cols-3 gap-4">                        <!-- Learning Guidance Section -->                        <div class="bg-white p-4 rounded-lg shadow">                            <div class="flex items-center justify-between mb-4">                                <h3 class="text-lg font-semibold text-gray-800">Learning Guide</h3>                            </div>                            <a                                class="flex items-center justify-between p-3 hover:bg-gray-50 rounded cursor-pointer"                                @click="visitPage(route('campus.courses.weeks.guides.weekGuide', { weekId: week.id,courseId: course.id }))"                            >                                <span class="text-indigo-600">View Learning Guides</span>                                <i class="fas fa-arrow-right text-gray-400"></i>                            </a>                        </div>                        <!-- Assignments Section -->                        <div class="bg-white p-4 rounded-lg shadow">                            <div class="flex items-center justify-between mb-4">                                <h3 class="text-lg font-semibold text-gray-800">Assignments</h3>                                <span class="bg-blue-100 text-blue-800 text-xs font-medium px-2.5 py-0.5 rounded">                                    {{ week.assignments?.length || 0 }}                                </span>                            </div>                            <a                                class="flex items-center justify-between p-3 hover:bg-gray-50 rounded cursor-pointer"                                @click="visitPage(route('campus.courses.weeks.assignments.index', { weekId: week.id,courseId: course.id }))"                            >                                <span class="text-indigo-600">View Assignments</span>                                <i class="fas fa-arrow-right text-gray-400"></i>                            </a>                        </div>                        <!-- Quizzes Section -->                        <div class="bg-white p-4 rounded-lg shadow">                            <div class="flex items-center justify-between mb-4">                                <h3 class="text-lg font-semibold text-gray-800">Quizzes</h3>                                <span class="bg-green-100 text-green-800 text-xs font-medium px-2.5 py-0.5 rounded">                                    {{ week.quizzes?.length || 0 }}                                </span>                            </div>                            <a                                class="flex items-center justify-between p-3 hover:bg-gray-50 rounded cursor-pointer"                                @click="visitPage(route('campus.courses.weeks.quiz.index', { weekId: week.id, courseId: course.id }))"                            >                                <span class="text-indigo-600">View Quizzes</span>                                <i class="fas fa-arrow-right text-gray-400"></i>                            </a>                        </div>                    </div>                    <!-- Optional: Preview of Latest Items -->                    <div class="mt-6 space-y-4">                        <!-- Latest Assignment Preview -->                        <div v-if="week.assignments?.length" class="border-l-4 border-blue-500 pl-4">                            <h4 class="text-sm font-medium text-gray-500">Latest Assignment</h4>                            <p class="text-gray-800">{{ week.assignments[0]?.title }}</p>                        </div>                        <!-- Latest Quiz Preview -->                        <div v-if="week.quizzes?.length" class="border-l-4 border-green-500 pl-4">                            <h4 class="text-sm font-medium text-gray-500">Latest Quiz</h4>                            <p class="text-gray-800">{{ week.quizzes[0]?.title }}</p>                        </div>                        <!-- Latest Announcement Preview -->                        <div v-if="week.announcements?.length" class="border-l-4 border-yellow-500 pl-4">                            <h4 class="text-sm font-medium text-gray-500">Latest Announcement</h4>                            <p class="text-gray-800">{{ week.announcements[0]?.title }}</p>                        </div>                    </div>                </div>            </div>        </section>    </div></template><script>import {route} from "ziggy-js";export default {    name: "CoursePageWithContent",    props: {        weeks: {            type: Array,            required: true        },        course: {            type: Object,            required: true        },        learningGuidance: Array,        courseAnnouncements: {            type: Array,            default: () => []        },        courseResources: {            type: Array,            default: () => []        }    },    data() {        return {            currentTab: 'announcements',            tabs: [                {                    id: 'announcements',                    name: 'Announcements',                    url: route('campus.course.announcements.index', {id: this.course.id})                },                {                    id: 'resources',                    name: 'Resources',                    url: route('campus.resources', {id: this.course.id})                },                {                    id: 'syllabus',                    name: 'Syllabus',                    url: route('campus.syllabus', {id: this.course.id})                },                {                    id: 'learning',                    name: 'Learning Guidance',                    url: route('campus.courses.weeks.guides.index',                        {courseId: this.course.id, weekId: this.course.id})                }            ]        }    },    mounted() {        this.weeks.forEach(week => {            week.isOpen = false;        });    },    methods: {        route,        toggleWeek(index) {            this.weeks[index].isOpen = !this.weeks[index].isOpen;        },        visitPage(url) {            this.$inertia.visit(url);        }    }};</script>